import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

const ArticleList = () => {
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchArticles = async () => {
            try {
                const response = await api.get('/articles');
                const allArticles = response.data;

                // Deduplicate by URL
                const uniqueArticles = [];
                const seenUrls = new Set();

                for (const article of allArticles) {
                    if (article.url && !seenUrls.has(article.url)) {
                        seenUrls.add(article.url);
                        uniqueArticles.push(article);
                    }
                }

                setArticles(uniqueArticles);
            } catch (error) {
                console.error("Failed to fetch articles", error);
            } finally {
                setLoading(false);
            }
        };

        fetchArticles();
    }, []);

    if (loading) return <div>Loading...</div>;

    return (
        <div style={{ padding: '20px', fontFamily: 'sans-serif' }}>
            <h1>Articles ({articles.length})</h1>
            <div style={{ display: 'grid', gap: '20px', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))' }}>
                {articles.map((article) => (
                    <div key={article._id} style={{ border: '1px solid #ddd', padding: '15px', borderRadius: '8px' }}>
                        {article.image && (
                            <img
                                src={article.image}
                                alt={article.title}
                                style={{ width: '100%', height: '200px', objectFit: 'cover', borderRadius: '4px' }}
                            />
                        )}
                        <h3 style={{ margin: '10px 0' }}>{article.title || 'Untitled'}</h3>
                        <a
                            href={article.url}
                            target="_blank"
                            rel="noopener noreferrer"
                            style={{ display: 'block', marginBottom: '10px', color: 'blue', textDecoration: 'none' }}
                        >
                            {article.url}
                        </a>
                        <button
                            onClick={() => navigate(`/article/${article._id}`)}
                            style={{ padding: '8px 16px', cursor: 'pointer', background: '#f0f0f0', border: '1px solid #ccc', borderRadius: '4px' }}
                        >
                            Open
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ArticleList;
